-- Add new columns to compounds table
ALTER TABLE compounds ADD COLUMN IF NOT EXISTS what_it_is TEXT;
ALTER TABLE compounds ADD COLUMN IF NOT EXISTS side_effects TEXT;
ALTER TABLE compounds ADD COLUMN IF NOT EXISTS sources TEXT[];

-- Insert compounds (ON CONFLICT updates existing rows)
ON CONFLICT (name) DO UPDATE SET
  category = EXCLUDED.category,
  what_it_is = EXCLUDED.what_it_is,
  common_uses = EXCLUDED.common_uses,
  risk_score = EXCLUDED.risk_score,
  side_effects = EXCLUDED.side_effects,
  affected_systems = EXCLUDED.affected_systems,
  nutrition_impact_summary = EXCLUDED.nutrition_impact_summary,
  sources = EXCLUDED.sources,
  updated_at = NOW();
